<?php if (count($posts)): ?><?php $this->beginWidget('DPortlet', array('title' => null));?><h4>Последние записи:</h4><?php foreach ($posts as $post): ?>	<div class="entry last">		<?php if ($post->image): ?>			<p class="thumb"><?php echo CHtml::image($post->getImageThumbUrl(100, 100), ''); ?></p>		<?php endif; ?>		<div class="title"><a href="<?php echo $post->url; ?>"><?php echo CHtml::encode($post->title); ?></a></div>		<!--noindex--><div class="short"><?php echo trim($post->short_purified); ?></div><!--/noindex-->	</div><?php endforeach; ?><?php $this->endWidget(); ?><?php endif; ?>