<?php if (count($posts)): ?><?php $this->beginWidget('DPortlet', array('title' => null));?><?php if ($category->id): ?>	<h4>Другие статьи раздела <a rel="nofollow" href="<?php echo $category->url; ?>"><?php echo CHtml::encode($category->title); ?></a>:</h4><?php else: ?>	<h4>Другие статьи:</h4><?php endif ?><?php foreach ($posts as $post): ?><div class="entry last">    <?php if ($post->image): ?>    <p class="thumb"><a rel="nofollow" href="<?php echo $post->url; ?>"><?php echo CHtml::image($post->getImageThumbUrl(100, 100), $post->image_alt); ?></a></p>    <?php endif; ?>    <h3><a href="<?php echo $post->url; ?>"><?php echo CHtml::encode($post->title); ?></a></h3>    <div class="short"><?php echo trim($post->short_purified); ?></div>    <div class="clear"></div></div><?php endforeach; ?><?php $this->endWidget(); ?><?php endif; ?>