<?php if (count($posts)): ?><?php $this->beginWidget('DPortlet', array('title' => null));?><h4>Другие статьи раздела <a href="<?php echo $category->url; ?>"><?php echo CHtml::encode($category->title); ?></a>:</h4><?php foreach ($posts as $post): ?><div class="entry last">    <?php if ($post->image): ?>    <p class="thumb"><a href="<?php echo $post->url; ?>"><img src="<?php echo $post->getImageThumbUrl(100, 100); ?>" alt="<?php echo $post->image_alt; ?>" /></a></p>    <?php endif; ?>    <h3><a href="<?php echo $post->url; ?>"><?php echo CHtml::encode($post->title); ?></a></h3>    <div class="short"><p><?php echo trim($post->short_purified); ?></p></div>    <div class="clear"></div>    <div class="info nomargin">    </div></div><?php endforeach; ?><?php $this->endWidget(); ?><?php endif; ?>