<div class="books">    <?php foreach ($books as $i=>$item): ?>    <div class="book item_<?php echo $i+1; ?>">        <?php echo CHtml::link(CHtml::image($item->imageUrl, $item->title), $item->url, array('rel'=>'nofollow')); ?>        <p class="title"><?php echo CHtml::link(CHtml::encode($item->title), $item->url, array('rel'=>'nofollow')); ?></p>        <p class="author"><?php echo CHtml::encode($item->author); ?></a></p>        <div class="short">            <?php echo $item->short_purified; ?>        </div>        <div class="clear"></div>    </div>    <?php endforeach; ?>    <div class="clear"></div>    <p class="show_all"><a rel="nofollow" href="<?php echo Yii::app()->createUrl('booksru/book/all'); ?>">Посмотреть остальные &raquo;</a></p></div>