<div class="books">    <?php foreach ($books as $i=>$item): ?>    <div class="book item_<?php echo $i+1; ?>">        <span data-href="<?php echo CHtml::encode($item->url); ?>"><?php echo CHtml::image($item->imageUrl, $item->title); ?></span>        <p class="title"><span data-href="<?php echo CHtml::encode($item->url); ?>"><?php echo CHtml::encode($item->title); ?></span></p>        <p class="author"><?php echo CHtml::encode($item->author); ?></p>        <div class="short">            <?php echo $item->short_purified; ?>        </div>        <div class="clear"></div>    </div>    <?php endforeach; ?>    <div class="clear"></div>    <p class="show_all"><span data-href="<?php echo Yii::app()->createUrl('interest/item/all'); ?>">Посмотреть остальные &raquo;</span></p></div>