<div class="page_widget">    <div class="body">        <div class="wrapper">            <?php foreach ($news as $new): ?>                <p class="pub_title"><a href="<?php echo $new->url; ?>"><?php echo $new->title; ?></a></p>                <p class="pub_info"><?php echo DDateHelper::normdate($new->date); ?></p>                <?php if ($new->image): ?>                    <p class="pub_preview"><a href="<?php echo $new->url; ?>"><img src="<?php echo Yii::app()->request->baseUrl . '/' .News::IMAGE_PATH . '/small_' . $new->image; ?>" alt="" /></a></p>                    <?php endif; ?>                <div class="pub_short"><?php echo $new->short; ?></div>                <p class="pub_link"><a rel="nofollow" href="<?php echo $new->url; ?>">Подробнее</a></p>                <div class="clear"></div>            <?php endforeach; ?>        </div>    </div></div>